#!/bin/bash\n\n# Load environment variables if .env file exists\nif [ -f .env ]; then\n    source .env\nfi\n\n# Default values if not set in environment\nDB_HOST=${TEST_DB_HOST:-localhost}\nDB_PORT=${TEST_DB_PORT:-3306}\nDB_USER=${TEST_DB_USER:-root}\nDB_PASSWORD=${TEST_DB_PASSWORD:-root1234}\nDB_NAME=${TEST_DB_NAME:-smart_campus_test}\n\n# Print the configuration being used\necho "Initializing test database with following configuration:"\necho "Host: $DB_HOST"\necho "Port: $DB_PORT"\necho "User: $DB_USER"\necho "Database: $DB_NAME"\n\n# Execute the schema file\nmysql -h "$DB_HOST" -P "$DB_PORT" -u "$DB_USER" -p"$DB_PASSWORD" < tests/schema/test_schema.sql\n\n# Check if the schema execution was successful\nif [ $? -eq 0 ]; then\n    echo "Test database initialized successfully"\nelse\n    echo "Error initializing test database"\n    exit 1\nfi
